function WebDKP_Standby_GUIToggle(a,b)local c=WebDKP_GetTableid()if b==nil or b==""then b=WebDKP_Standby_FrameAddStandby:GetText()end;if b==nil or b==""then playernametable=WebDKP_GetSelectedPlayers(1)if playernametable~=nil then b=playernametable[0]["name"]end end;if b==nil or b==""then WebDKP_Print("你必须输入一个名字或选择一个玩家以将其添加或删除到替补列表中.")else if a=="add"then if WebDKP_DkpTable[b]==nil then WebDKP_DkpTable[b]={["dkp_"..c]=0,["class"]="",["standby"]=1,["cantrim"]=false}WebDKP_SendWhisper(b,"你现在被列为替补玩家,请遵守本公会替补守则!")else WebDKP_DkpTable[b]["standby"]=1;WebDKP_SendWhisper(b,"你现在被列为替补玩家,请遵守本公会替补守则!")end end;if a=="remove"then if WebDKP_DkpTable[b]==nil then WebDKP_Print("此人不在你的表中")else WebDKP_DkpTable[b]["standby"]=0;WebDKP_SendWhisper(b,"你已被移除为替补玩家")end end;WebDKP_UpdateTableToShow()WebDKP_UpdateTable()end end;function WebDKP_Standby_Reset()for d,e in pairs(WebDKP_DkpTable)do if type(e)=="table"then e["standby"]=0 end end;WebDKP_UpdateTableToShow()WebDKP_UpdateTable()end
